INSERT INTO `multipurpose` VALUES (1,'aaaaaaaaaaa','aaaaaaaaaaaaaaa',1,'2013-10-21 06:40:44',NULL),(2,'啥啥啥','<div style=\"text-align:left;\">\r\n	<span style=\"line-height:1.5;\">的飞凤飞飞飞上的说法是是都挂</span>\r\n</div>',1,'2013-10-21 06:41:31',NULL),(3,'的飞洒发','都封杀封杀费',2,'2013-10-21 06:51:25',NULL),(4,'的飞洒发','都封杀封杀费',2,'2013-10-21 06:52:15',NULL),(5,'公示通知2','<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"/cxzx/attached/image/20131021/20131021145408_686.png\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>',2,'2013-10-21 06:54:53',NULL),(6,'公示通知2','<p>\r\n	<span style=\"line-height:1.5;\"><img src=\"/cxzx/attached/image/20131021/20131021145408_686.png\" alt=\"\" /></span>\r\n</p>\r\n<p>\r\n	<span style=\"line-height:1.5;\"><br />\r\n</span>\r\n</p>',2,'2013-10-21 06:55:09',NULL),(7,'阿萨','<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">飞洒发的</span>\r\n</div>',1,'2013-10-21 07:17:22',NULL),(8,'阿萨','<div style=\"text-align:center;\">\r\n	<span style=\"line-height:1.5;\">飞洒发的&lt;</span>\r\n</div>',1,'2013-10-21 07:17:28',NULL),(9,'中心概况','<div id=\"new_text\" style=\"margin:10px;text-align:left;color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<p class=\"1\" style=\"text-align:center;text-indent:28px;\">\r\n		<span style=\"font-family:宋体;font-size:10.5pt;\"><img alt=\"\" src=\"http://itcs.tsinghua.edu.cn/uploadfile/2010/0727/20100727115340762.jpg\" style=\"width:396px;height:261px;\" /><br />\r\n<br />\r\n<br />\r\n</span> \r\n	</p>\r\n	<p class=\"1\" style=\"text-align:left;text-indent:28px;\">\r\n		<span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">清华大学理论计算机科学研究中心（ITCS）是由唯一亚裔图灵奖获得者姚期智教授回国后创办的学术机构，主要从事计算机科学研究与教育。中心的宗旨是通过培养优秀的学生与研究人员，取得具有世界影响力的科研成果，为中国创建世界一流大学做出贡献。中心的目标是为中国计算机科学的研究和教育打造一条“超级公路”，成为计算机学科领域的世界领军团队。</span> \r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:28px;\">\r\n		<span><span style=\"font-family:宋体;font-size:10.5pt;\"><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">在姚期智先生的带领下，清华大学理论计算机科学研究中心组建了强大的国际化师资团队：</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(1)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">多位</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授、副教授</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">、</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">助理教授等组成的全职师资队伍</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(2)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">每年在</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">世界范围内吸引和选拔国际一流大学优秀博士毕业生来华从事博士后研究；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(3)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">长期</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">邀请知名教授来访讲学；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(4)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">从</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">MSRA</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">等业界知名研究院聘请资深研究人士担任兼职教授和兼职教师；</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">(5)</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">组建了一支囊括全球顶尖计算机科学家的理论计算机科学讲席教授组，包括普林斯顿大学</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">Sanjeev Arora</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授、</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">Bernard Chazelle</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授，美国麻省理工学院</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">Silvio Micali</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授等，共同致力于培养一流人才。</span></span></span> \r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:28px;\">\r\n		<span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">截至</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">2010</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">年</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">7</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">月，计算机科学实验班（姚班）通过五载办学，已培养三届毕业生共</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">81</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">人，其中</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">96%</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">选择继续学术深造；中心培养的研究生已有</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">近20</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">人获得博士学位，正逐渐在普林斯顿等国际著名高校及微软亚洲研究院等世界知名科研机构崭露头角。</span></span> \r\n	</p>\r\n</div>\r\n<div id=\"new_text\" style=\"margin:10px;text-align:left;color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:24px;\">\r\n		<span><span style=\"line-height:28px;font-family:宋体;\"><span>清华大学理论计算机科学研究中心（ITCS）的成立和发展促成了国内理论计算机科学学科从无到有、从有到强的质变，吸引了越来越多的优秀青年学子投身到这一领域。中心长期以来得到国家领导人和清华大学的全力支持。温家宝总理等领导多次来访看望姚期智教授，并对中心的发展构想给予关注、寄予厚望。</span></span></span> \r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:24px;\">\r\n		<span><span style=\"line-height:28px;font-family:宋体;\"><span>2009年，姚期智先生提出了“中国计算机科学2020计划”的发展战略——集合中外学者的力量，有步骤地使中国在2020年成为计算机科学的一流基地。这一战略发展目标得到国家领导人的大力肯定和支持，目前正在全力推进中。根据中心的2020计划，在2010-2015年阶段将组建30名的教师队伍，培养120名博士生；到2015-2020年阶段，教师队伍将拓展为60名，拟培养240名博士生；中心的科研领域也将覆盖理论、安全密码学、网络、计算生物学、机器学习等五大方向。<span style=\"background-color:#E53333;\"></span><br />\r\n</span></span></span> \r\n	</p>\r\n</div>',11,'2013-10-22 06:21:17',NULL),(10,NULL,'<div id=\"new_text\" style=\"margin:10px;text-align:left;color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<p class=\"1\" style=\"text-align:center;text-indent:28px;\">\r\n		<span style=\"font-family:宋体;font-size:10.5pt;\"><img alt=\"\" src=\"http://itcs.tsinghua.edu.cn/uploadfile/2010/0727/20100727115340762.jpg\" style=\"width:396px;height:261px;\" /><br />\r\n<br />\r\n<br />\r\n</span>\r\n	</p>\r\n	<p class=\"1\" style=\"text-align:left;text-indent:28px;\">\r\n		<span style=\"font-family:宋体;font-size:10.5pt;\">清华大学理论计算机科学研究中心（ITCS）是由唯一亚裔图灵奖获得者姚期智教授回国后创办的学术机构，主要从事计算机科学研究与教育。中心的宗旨是通过培养优秀的学生与研究人员，取得具有世界影响力的科研成果，为中国创建世界一流大学做出贡献。中心的目标是为中国计算机科学的研究和教育打造一条“超级公路”，成为计算机学科领域的世界领军团队。</span>\r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:28px;\">\r\n		<span><span style=\"font-family:宋体;font-size:10.5pt;\"><span style=\"font-family:宋体;font-size:10.5pt;\">在姚期智先生的带领下，清华大学理论计算机科学研究中心组建了强大的国际化师资团队：</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">(1)<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">多位</span><span style=\"font-family:宋体;font-size:10.5pt;\">教授、副教授</span><span style=\"font-family:宋体;font-size:10.5pt;\">、</span><span style=\"font-family:宋体;font-size:10.5pt;\">助理教授等组成的全职师资队伍</span><span style=\"font-family:宋体;font-size:10.5pt;\">；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">(2)<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">每年在</span><span style=\"font-family:宋体;font-size:10.5pt;\">世界范围内吸引和选拔国际一流大学优秀博士毕业生来华从事博士后研究；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">(3)<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">长期</span><span style=\"font-family:宋体;font-size:10.5pt;\">邀请知名教授来访讲学；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">(4)<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">从</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">MSRA</span><span style=\"font-family:宋体;font-size:10.5pt;\">等业界知名研究院聘请资深研究人士担任兼职教授和兼职教师；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">(5)</span><span style=\"font-family:宋体;font-size:10.5pt;\">组建了一支囊括全球顶尖计算机科学家的理论计算机科学讲席教授组，包括普林斯顿大学</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">Sanjeev Arora<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">教授、</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">Bernard Chazelle<span class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;\">教授，美国麻省理工学院</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">Silvio Micali</span><span style=\"font-family:宋体;font-size:10.5pt;\">教授等，共同致力于培养一流人才。</span></span></span>\r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:28px;\">\r\n		<span><span style=\"font-family:宋体;font-size:10.5pt;\">截至</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">2010</span><span style=\"font-family:宋体;font-size:10.5pt;\">年</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">7</span><span style=\"font-family:宋体;font-size:10.5pt;\">月，计算机科学实验班（姚班）通过五载办学，已培养三届毕业生共</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">81</span><span style=\"font-family:宋体;font-size:10.5pt;\">人，其中</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">96%</span><span style=\"font-family:宋体;font-size:10.5pt;\">选择继续学术深造；中心培养的研究生已有</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\">近20</span><span style=\"font-family:宋体;font-size:10.5pt;\">人获得博士学位，正逐渐在普林斯顿等国际著名高校及微软亚洲研究院等世界知名科研机构崭露头角。</span></span>\r\n	</p>\r\n</div>\r\n<div id=\"new_text\" style=\"margin:10px;text-align:left;color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:24px;\">\r\n		<span><span style=\"line-height:28px;font-family:宋体;\"><span>清华大学理论计算机科学研究中心（ITCS）的成立和发展促成了国内理论计算机科学学科从无到有、从有到强的质变，吸引了越来越多的优秀青年学子投身到这一领域。中心长期以来得到国家领导人和清华大学的全力支持。温家宝总理等领导多次来访看望姚期智教授，并对中心的发展构想给予关注、寄予厚望。</span></span></span>\r\n	</p>\r\n	<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:24px;\">\r\n		<span><span style=\"line-height:28px;font-family:宋体;\"><span>2009年，姚期智先生提出了“中国计算机科学2020计划”的发展战略——集合中外学者的力量，有步骤地使中国在2020年成为计算机科学的一流基地。这一战略发展目标得到国家领导人的大力肯定和支持，目前正在全力推进中。根据中心的2020计划，在2010-2015年阶段将组建30名的教师队伍，培养120名博士生；到2015-2020年阶段，教师队伍将拓展为60名，拟培养240名博士生；中心的科研领域也将覆盖理论、安全密码学、网络、计算生物学、机器学习等五大方向。</span></span></span>\r\n	</p>\r\n</div>',1,'2013-10-21 09:09:56',NULL),(12,'理事会','<h1 align=\"center\">\r\n	<div id=\"new_text\" style=\"margin:10px;text-align:left;color:#000000;font-family:Arial, Helvetica, sans-serif;font-size:14px;font-style:normal;font-weight:normal;background-color:#FFFFFF;\">\r\n		<p class=\"1\" style=\"text-align:center;text-indent:28px;\">\r\n			<span style=\"font-family:宋体;font-size:10.5pt;\"><img alt=\"\" src=\"http://itcs.tsinghua.edu.cn/uploadfile/2010/0727/20100727115340762.jpg\" style=\"width:396px;height:261px;\" /><br />\r\n<br />\r\n<br />\r\n</span> \r\n		</p>\r\n		<p class=\"1\" style=\"text-align:left;text-indent:28px;\">\r\n			<span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">清华大学理论计算机\r\n科学研究中心（ITCS）是由唯一亚裔图灵奖获得者姚期智教授回国后创办的学术机构，主要从事计算机科学研究与教育。中心的宗旨是通过培养优秀的学生与研\r\n究人员，取得具有世界影响力的科研成果，为中国创建世界一流大学做出贡献。中心的目标是为中国计算机科学的研究和教育打造一条“超级公路”，成为计算机学\r\n科领域的世界领军团队。</span> \r\n		</p>\r\n		<p class=\"MsoListParagraph\" style=\"text-align:left;text-indent:28px;\">\r\n			<span><span style=\"font-family:宋体;font-size:10.5pt;\"><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">在姚期智先生的带领下，清华大学理论计算机科学研究中心组建了强大的国际化师资团队：</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(1)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">多位</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授、副教授</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">、</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">助理教授等组成的全职师资队伍</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(2)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">每年在</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">世界范围内吸引和选拔国际一流大学优秀博士毕业生来华从事博士后研究；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(3)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">长期</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">邀请知名教授来访讲学；</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">(4)</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">从</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">MSRA</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">等业界知名研究院聘请资深研究人士担任兼职教授和兼职教师；</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">(5)</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">组建了一支囊括全球顶尖计算机科学家的理论计算机科学讲席教授组，包括普林斯顿大学</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">Sanjeev Arora</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授、</span><span style=\"font-family:\'Times New Roman\', serif;font-size:10.5pt;\"><span style=\"line-height:2;\">Bernard Chazelle</span><span style=\"line-height:2;\" class=\"Apple-converted-space\">&nbsp;</span></span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授，美国麻省理工学院</span><span style=\"font-family:\'Times New Roman\',serif;font-size:10.5pt;line-height:2;\">Silvio Micali</span><span style=\"font-family:宋体;font-size:10.5pt;line-height:2;\">教授等，共同致力于培养一流人才。</span></span></span> \r\n		</p